mapper.select:
SELECT
,
, p.user_id AS P_user_id FROM hangang.user AS u JOIN hangang.lecture_bank_purchase AS p ON u.id = p.user_id JOIN hangang.lecture_bank AS b ON p.lecture_bank_id = b.id JOIN hangang.lecture AS l ON b.lecture_id = l.id WHERE u.id=#{id} and u.is_deleted = 0 and p.is_deleted = 0 and b.is_deleted = 0;
